jQuery(window).on("load", function() {
    $('#preloader').fadeOut(500);
    $('#main-wrapper').addClass('show');

    $('body').attr('data-sidebar-style') === "mini" ? $(".hamburger").addClass('is-active') : $(".hamburger").removeClass('is-active')
});

(function($) {
    "use strict";



    /*
    -------------------
    List item active
    -------------------*/
    $('.icons')
        .on('click', function() {
            $(".icons.active")
                .toggleClass("active");
            $(this)
                .addClass('active');
        });

    $(".icons")
        .on("click", function(event) {
            event.stopPropagation();
        });

    $(document)
        .on("click", function() {
            $(".icons")
                .removeClass("active");
        });


    $('.notify-close').click(function() {
        $(this).parent().fadeOut(500);
    });

    // bootstrap select
    // $('.selectpicker').selectpicker();


    $("#menu").metisMenu();

    $('.nk-nav-scroll').slimscroll({
        position: "right",
        size: "5px",
        height: "100%",
        color: "transparent"
    });

    $('.sidebar-right-inner').slimscroll({
        // position: "left",
        size: "5px",
        height: "100%",
        color: "#c6c8c9"
    });

    $('.event-msg-left').slimscroll({
        position: "right",
        size: "5px",
        height: "700px",
        color: "#c6c8c9"
    });

    $('.event-chat-ryt ul').slimscroll({
        position: "right",
        size: "5px",
        height: "521px",
        color: "#c6c8c9"
    });


    $(".nav-control").on('click', function() {
        $('#main-wrapper').toggleClass("menu-toggle");

        $(".hamburger").toggleClass("is-active");
    });


    $(function() {
        for (var nk = window.location, 
            o = $("ul#menu a").filter(function() {
                    return this.href == nk;
                })
                .addClass("active")
                .parent()
                .addClass("active");;) {
            if (!o.is("li")) break;
            o = o.parent()
                .addClass("in")
                .parent()
                .addClass("active");
        }
    });

    $(function() {
        // var win_w = window.outerWidth;
        var win_h = window.outerHeight;
        
        // if (win_w <= 1199) {
        //     $("body").addClass("mini-nav");

        // } else {
        //     $("body").removeClass("mini-nav");
        // };
        var win_h = window.outerHeight;
        if (win_h > 0 ? win_h : screen.height) {
            $(".content-body").css("min-height", (win_h + 60) + "px");
        };
    });






    $('[data-toggle="popover"]').popover();
    $('[data-toggle="tooltip"]').tooltip();


    $('a[data-action="collapse"]').on("click", function(i) {
        i.preventDefault(),
            $(this).closest(".card").find('[data-action="collapse"] i').toggleClass("ti-minus ti-plus"),
            $(this).closest(".card").children(".card-body").collapse("toggle");
    });

    $('a[data-action="expand"]').on("click", function(i) {
        i.preventDefault(),
            $(this).closest(".card").find('[data-action="expand"] i').toggleClass("icon-size-actual icon-size-fullscreen"),
            $(this).closest(".card").toggleClass("card-fullscreen");
    });



    $('[data-action="close"]').on("click", function() {
        $(this).closest(".card").removeClass().slideUp("fast");
    });

    $('[data-action="reload"]').on("click", function() {
        var e = $(this);
        e.parents(".card").addClass("card-load"),
            e.parents(".card").append('<div class="card-loader"><i class=" ti-reload rotate-refresh"></div>'),
            setTimeout(function() {
                e.parents(".card").children(".card-loader").remove(),
                    e.parents(".card").removeClass("card-load")
            }, 2000)
    });

    const headerHight = $('.header').innerHeight();

    $(window).scroll(function() {
        if($('body').attr('data-layout') === "horizontal" && $('body').attr('data-header-position') === "static" && $('body').attr('data-sidebar-position') === "fixed")
            $(this.window).scrollTop() >= headerHight ? $('.metismenu').addClass('fixed') :  $('.metismenu').removeClass('fixed')
    });

    $('.sidebar-right-trigger').on('click', function() {
        $('.sidebar-right').toggleClass('show');
    });



    /*==============================================
		checkbox and star icon
	===============================================*/

    $("#trigger").click(function(){
        $(".individual").prop("checked",$(this).prop("checked"));
    });


    $(".email__star .fa-star").click(function() {
        $(this).toggleClass("far fas");
    });



    //set fullscreen mood
    const elem = document.documentElement;
    /* View in fullscreen */
    function openFullscreen() {
        if (elem.requestFullscreen) {
            elem.requestFullscreen();
        } else if (elem.mozRequestFullScreen) { /* Firefox */
            elem.mozRequestFullScreen();
        } else if (elem.webkitRequestFullscreen) { /* Chrome, Safari and Opera */
            elem.webkitRequestFullscreen();
        } else if (elem.msRequestFullscreen) { /* IE/Edge */
            elem.msRequestFullscreen();
        }
    }

    /* Close fullscreen */
    function closeFullscreen() {
        if (document.exitFullscreen) {
        document.exitFullscreen();
        } else if (document.mozCancelFullScreen) { /* Firefox */
        document.mozCancelFullScreen();
        } else if (document.webkitExitFullscreen) { /* Chrome, Safari and Opera */
        document.webkitExitFullscreen();
        } else if (document.msExitFullscreen) { /* IE/Edge */
        document.msExitFullscreen();
        }
    }

    $('.window_fullscreen-x').on('click', function() {
        document.fullscreen ? closeFullscreen() : openFullscreen();
    });




})(jQuery);




